import Vue from 'vue'
import VueRouter from 'vue-router'
import Login from '../views/Login'
import Home from '../views/Home'
import Ordering from '../views/Ordering'
import Orderingmgt from '../views/tea/Orderingmgt'
import advertising from '../views/advertising'
import OrderList from '../views/OrderList'

Vue.use(VueRouter)

const routes = [
    {
        path: '/',
        name: 'Login',
        component: Login,
        hidden: true,
    },
    {
        path: '/home',
        name: '教师功能',
        component: Home,
        children: [
            {
                path: '/orderingmgt',
                name: '订货会',
                component: Orderingmgt
            },
        ]
    },
    {
        path: '/home',
        name: '学生功能',
        component: Home,
        children: [
            {

            },
            {
                path: '/advertising',
                name: '广告投放',
                component: advertising
            },
            {
                path: '/ordering',
                name: '订货中心',
                component: Ordering
            },
            {
                path: '/orderlist',
                name: '已选订单',
                component: OrderList
            },
        ]
    },
]

const router = new VueRouter({
    routes
})

export default router
