const user = {
  state: null != window.sessionStorage.getItem('state') ? JSON.parse(window.sessionStorage.getItem('state')) : {
    user: {
      role: '',
      username: '',
      password: '',
      
    }
  },

  getters: {
    getUser(state) {
      return state.user;
    }
  },

  mutations: {
    updateUser(state, user) {
      state.user = user;
    }
  },

  actions: {
    asyncUpdateUser(context, user) {
      context.commit('updateUser', user);
    }
  }
}

export default user;
