import Vue from 'vue'
import VueRouter from 'vue-router'
import Login from '../views/Login'
import Home from '../views/Home'
import Orderingmgt from '../views/tea/Orderingmgt'
import Classmgt from '../views/tea/Classmgt'
import advertising from '../views/stu/advertising'
import OrderList from '../views/stu/OrderList'
import Ordering from '../views/stu/Ordering'
import Orderall from '../views/stu/Orderall'


Vue.use(VueRouter)

const routes = [
    {
        path: '/',
        name: 'Login',
        component: Login,
        hidden: true,
    },
    {
        path: '/home',
        name: '教师功能',
        component: Home,
        mate:{
            title:''
        },
        children: [
            {
                path: '/classmgt',
                name: '班级管理',
                component: Classmgt
            },
            {
                path: '/orderingmgt',
                name: '订货会',
                component: Orderingmgt
            },
        ]
    },
    {
        path: '/home',
        name: '学生功能',
        component: Home,
        children: [
            {
                path: '/orderall',
                name: '订货会',
                component: Orderall,
                children: [
                    {
                        path: '/advertising',
                        name: '广告投放',
                        component: advertising
                    },
                    {
                        path: '/ordering',
                        name: '订货中心',
                        component: Ordering
                    },
                ]
            },
            
            {
                path: '/orderlist',
                name: '已选订单',
                component: OrderList
            },
        ]
    },
]

const router = new VueRouter({
    routes
})

export default router
