const adv = {
    advstate: null != window.sessionStorage.getItem('state') ? JSON.parse(window.sessionStorage.getItem('state')) : {
      adv: {
        advShowFlag: 'false',
      }
    },
  
    getters: {
      getAdv(state) {
        return state.adv;
      }
    },
  
    mutations: {
      updateAdv(state, adv) {
        state.adv = adv;
      }
    },
  
    actions: {
      asyncUpdateAdv(context, adv) {
        context.commit('updateAdv', adv);
      }
    }
  }
  
  export default adv;
