import Vue from 'vue'
import VueRouter from 'vue-router'
import Login from '../views/Login'
import Home from '../views/Home'
import Ordering from '../views/Ordering'
import Groupmgt from '../views/Groupmgt'
import Orderingmgt from '../views/Orderingmgt'
import Advexp from '../views/Advexp'
import OrderList from '../views/OrderList'

Vue.use(VueRouter)

const routes = [
    {
        path: '/',
        name: 'Login',
        component: Login,
        hidden: true,
    },
    {
        path: '/home',
        name: '教师功能',
        component: Home,
        children:[
            {
                path: '/groupmgt',
                name: '分组管理',
                component: Groupmgt
            },
            {
                path: '/orderingmgt',
                name: '订货会',
                component: Orderingmgt
            },
            {
                path: '/orderingmgt',
                name: '订货会',
                component: Orderingmgt
            },
        ]
    },
    {
        path: '/home',
        name: '学生功能',
        component: Home,
        children:[
            {
                path: '/advexp',
                name: '广告费提交',
                component: Advexp
            },
            {
                path: '/ordering',
                name: '选择订单',
                component: Ordering
            },
            {
                path: '/orderlist',
                name: '已选订单',
                component: OrderList
            },
        ]
    },
]

const router = new VueRouter({
    routes
})

export default router
