import Vue from 'vue'
import VueRouter from 'vue-router'
import Login from '../views/Login'
import Home from '../views/Home'
import Orderingmgt from '../views/tea/Orderingmgt'
import Classmgt from '../views/tea/Classmgt'
import advertising from '../views/stu/advertising'
import OrderList from '../views/stu/OrderList'
import Ordering from '../views/stu/Ordering'
import Orderall from '../views/stu/Orderall'


Vue.use(VueRouter)

const routes = [
    {
        path: '/',
        name: 'Login',
        component: Login,
        hidden: true,
    },
    {
        path: '/home',
        name: '教师功能',
        component: Home,
        
        children: [
            {
                path: '/classmgt',
                name: '班级管理',
                component: Classmgt,
                mate:{
                    title:'班级管理'
                },
            },
            {
                path: '/orderingmgt',
                name: '订货会',
                component: Orderingmgt,
                mate:{
                    title:'订货会'
                },
            },
        ]
    },
    {
        path: '/home',
        name: '学生功能',
        component: Home,
        children: [
            {
                path: '/orderall',
                name: '订货会',
                component: Orderall,
                mate:{
                    title:'订货会'
                },
                children:[
                    {
                        path: '/advertising',
                        name: '广告投放',
                        component: advertising,
                        mate:{
                            title:'广告投放'
                        },
                    },
                    {
                        path: '/ordering',
                        name: '订货中心',
                        component: Ordering,
                        mate:{
                            title:'订货中心'
                        },
                    },
                ]
            },
            
            {
                path: '/orderlist',
                name: '已选订单',
                component: OrderList,
                mate:{
                    title:'已选订单'
                },
            },
        ]
    },
]

const router = new VueRouter({
    routes
})

// 解决vue-router在3.0版本以上重复报错问题
const originalPush = VueRouter.prototype.push
VueRouter.prototype.push = function push(location) {
  return originalPush.call(this, location).catch(err => err)
}


export default router
