import Vue from 'vue'
import Vuex from 'vuex'


Vue.use(Vuex);

const state={
    user:{
        name:''
    }
}

const getters={
    getUser(state){
        return state.user;
    }
}

const mutations={
    updateUser(state,user){
        state.user = user;
    }
}

const actions={
    asyncUpdateUser(context,user){
        context.commit('updateUser',user);
    }
}

export default Vuex.Store({
    state,
    getters,
    mutations,
    actions
})
